import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  username: string = '';
  password: string = '';
  registrationMessage: string = '';

  constructor(private http: HttpClient,private router: Router) {}

  registerUser() {
    this.router.navigate(['/register']); // Replace 'login' with the path you want to navigate to

    const userData = {
      username: this.username,
      password: this.password
    };



    this.http.post('http://localhost:4000/auth/signup', userData).subscribe(
      (response: any) => {
        // Assuming the server responds with a JWT token in the 'token' property.
        const token = response.token;
        this.registrationMessage = 'User registered successfully';
        // You can save the JWT token in a secure place, such as a cookie or local storage.
        // Example: localStorage.setItem('token', token);
        localStorage.setItem('token', token);

      },
      (error) => {
        this.registrationMessage = 'Registration failed. Please try again.';
      }
    );
  }
}
