import { Component } from '@angular/core';
import { Router,NavigationEnd } from '@angular/router';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'cycle-frontend';
  isHomePage: boolean = false;
  isAuthenticated: boolean = false;

  constructor(private router: Router) {
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        // Check the route to determine whether the user is authenticated
        this.isAuthenticated = this.isRouteAuthenticated(event.url);
      }
    });
  }

  isRouteAuthenticated(url: string): boolean {
    // You can implement your logic to check if the user is authenticated for a given route
    // For example, check if the route is not '/login' or '/signup'
    return !['/login', '/register','/'].includes(url);
  }
  logout() {
    // Perform any logout actions here (e.g., clearing user data, tokens, etc.)
    // For simplicity, we will clear a token from localStorage.
    localStorage.removeItem('token');

    // After logout, navigate to the home page (assuming your home page route is '/home').
    this.router.navigate(['']);
  }
}
