import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-calculate',
  templateUrl: './calculate.component.html',
  styleUrls: ['./calculate.component.css']
})
export class CalculateComponent {
  userLocation: { latitude: number; longitude: number } = { latitude: 0, longitude: 0 };
  chosenSpot: string = '';
  cyclingSpeed: number = 0;
  dailyCyclingHours: number = 0;
  estimatedTime: number | null = null;

  constructor(private http: HttpClient) {}

  calculateTime(): void {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found. Please log in.');
      return;
    }

    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);
    const options = { headers };

    // Adjust the URL to match your server's endpoint
    const url = 'http://localhost:4000/calculate';
    const requestBody = {
      userLocation: this.userLocation,
      chosenSpot: this.chosenSpot,
      cyclingSpeed: this.cyclingSpeed,
      dailyCyclingHours: this.dailyCyclingHours
    };

    this.http.post<number>(url, requestBody, options).subscribe(
      (response: number) => {
        this.estimatedTime = response;
      },
      (error) => {
        console.error('Calculation failed:', error);
        this.estimatedTime = null;
      }
    );
  }
}
