import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { SpotService } from 'your-spot-service'; // Import the service to fetch spot names.

@Component({
  selector: 'app-spot-list',
  templateUrl: './spot-list.component.html',
  styleUrls: ['./spot-list.component.css']
})
export class SpotListComponent implements OnInit {
  spotNames: string[] = [];

  constructor(private spotService: SpotService, private router: Router) {}

  ngOnInit(): void {
    this.spotService.getSpotNames().subscribe((names: string[]) => {
      this.spotNames = names;
    });
  }

  showSpotDetails(spotName: string): void {
    // Navigate to the spot details route with the spot name as a parameter
    this.router.navigate(['/spot-details', spotName]);
  }
}
