import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';
  loginMessage: string = '';

  constructor(private http: HttpClient, private router: Router) {}

  login() {
    // Create an object with the username and password
    const userData = {
      username: this.username,
      password: this.password
    };

    // Send a POST request to the server's login endpoint
    this.http.post('http://localhost:4000/auth/login', userData).subscribe(
      (response: any) => {
        // Assuming the server responds with a JWT token in the 'token' property
        const token = response.token;
        this.loginMessage=response.message;
        // Save the token in a secure place, such as a cookie or local storage.
       localStorage.setItem('token', token);

        // Redirect to the user's dashboard or another page upon successful login
        this.router.navigate(['/dashboard']);
      },
      (error) => {
        this.loginMessage = error.message;
      }
    );
  }
}
