import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
@Component({
  selector: 'app-spot-details',
  templateUrl: './spot-details.component.html',
  styleUrls: ['./spot-details.component.css']
})
export class SpotDetailsComponent implements OnInit {
  spotName: string;
  spotDetails: any;

  constructor(private route: ActivatedRoute,private router:Router, private http: HttpClient) {
    this.spotName = this.route.snapshot.params['name'];
  }

  navigateToSpotList(){
    this.router.navigate(['/spots']);

  }
  ngOnInit(): void {
    // Fetch spot details using JWT token authorization
    const token = localStorage.getItem('token'); // Assuming you've saved the token in local storage
    const headers = {
      Authorization: `${token}`
    };

    this.http.get<any>(`http://localhost:4000/spots/spotname/${this.spotName}`, { headers })
      .subscribe(
        (response) => {
          this.spotDetails = response;
        },
        (error) => {
          console.error(error);
        }
      );
  }
}
<div class="spot-details" *ngIf="spotDetails">
  <h2>{{ spotDetails.name }}</h2>
  <p>Latitude: {{ spotDetails.latitude }}</p>
  <p>Longitude: {{ spotDetails.longitude }}</p>
  <p>Accessible by Cycling: {{ spotDetails.accessible_by_cycling ? 'Yes' : 'No' }}</p>
  <button (click)="navigateToSpotList()">Spot List Page</button>
</div>
<div *ngIf="!spotDetails">
  <p>Spot not found or you do not have permission to access this spot.</p>
  <button (click)="navigateToSpotList()">Spot List Page</button>
</div>
