import { Component } from '@angular/core';
import { Router,NavigationEnd } from '@angular/router';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'cycle-frontend';
  isHomePage: boolean = false;

  constructor(private router: Router) {
    // Check the current route to determine if it's the home page
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.isHomePage = event.url === '';
      }
    });
  }
  logout() {
    // Perform any logout actions here (e.g., clearing user data, tokens, etc.)
    // For simplicity, we will clear a token from localStorage.
    localStorage.removeItem('token');

    // After logout, navigate to the home page (assuming your home page route is '/home').
    this.router.navigate(['']);
  }
}
