import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { SpotService } from '../spot.service';
@Component({
  selector: 'app-spot-list',
  templateUrl: './spot-list.component.html',
  styleUrls: ['./spot-list.component.css']
})
export class SpotListComponent implements OnInit {
  spotNames: string[] = [];

  constructor(private spotService: SpotService, private router: Router) {}

  ngOnInit(): void {
    this.spotService.getSpotNames().subscribe((names: string[]) => {
      this.spotNames = names;
    });
  }

  showSpotDetails(spotName: string): void {
    // Navigate to the spot details route with the spot name as a parameter
    this.router.navigate(['/spotsdetails/', spotName]);
  }
  logout(): void {
    // Navigate to the spot details route with the spot name as a parameter
    this.router.navigate(['/spotsdetails/']);
  }
}
<div class="container">
  <div class="left-panel">
    <h2>Spot List</h2>
    <p>Click on list item for details</p>
    <ul>
      <li *ngFor="let name of spotNames" (click)="showSpotDetails(name)">{{ name }}</li>
    </ul>
    <button (click)="logout()" class="logout">Logout</button>
  </div>
  <div class="right-panel">
    <app-calculate></app-calculate>
  </div>
</div>
