import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-spot-details',
  templateUrl: './spot-details.component.html',
  styleUrls: ['./spot-details.component.css']
})
export class SpotDetailsComponent implements OnInit {
  spotName: string;
  spotDetails: any;

  constructor(private route: ActivatedRoute, private http: HttpClient) {
    this.spotName = this.route.snapshot.params['name'];
  }

  ngOnInit(): void {
    // Fetch spot details using JWT token authorization
    const token = localStorage.getItem('token'); // Assuming you've saved the token in local storage
    const headers = {
      Authorization: `Bearer ${token}`
    };

    this.http.get<any>(`http://localhost:4000/spot-details/${this.spotName}`, { headers })
      .subscribe(
        (response) => {
          this.spotDetails = response;
        },
        (error) => {
          console.error(error);
        }
      );
  }
}
