import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { baseUrl } from '../app.component';

@Component({
  selector: 'app-calculate',
  templateUrl: './calculate.component.html',
  styleUrls: ['./calculate.component.css']
})
export class CalculateComponent {
  estimatedTime: { estimatedTimeInHours: number } | null = null;

  userLocation: { latitude: number; longitude: number } = { latitude: 0, longitude: 0 };
  chosenSpot: string = '';
  cyclingSpeed: number = 0;
  dailyCyclingHours: number = 0;
  accessibleMessage:string='';
  resultTime:number=0;
  timeHour:number=0;
  spotDetails: any[] = []; 
  constructor(private http: HttpClient) {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found. Please log in.');
      return;
    }

    const headers = new HttpHeaders().set('Authorization', `${token}`);
    const options = { headers };

    this.http.get(baseUrl + ' /spots/getallspots', options).subscribe(
      (response: any) => {
        this.spotDetails = response;
      },
      (error) => {
        console.error('Failed to fetch spot details:', error);
      }
    );
  }

  calculateTime(): void {
    const selectedSpot = this.spotDetails.find((spot) => spot.name === this.chosenSpot);


    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found. Please log in.');
      return;
    }

    const headers = new HttpHeaders().set('Authorization', `${token}`);
    const options = { headers };

    const url = baseUrl + ' /spots/calculatebyaccesible';
    const requestBody = {
      userLocation: {
        latitude: this.userLocation.latitude,
        longitude: this.userLocation.longitude
      },
      chosenSpot: {
        latitude: selectedSpot.latitude,
        longitude: selectedSpot.longitude,
        accessible_by_cycling: selectedSpot.accessible_by_cycling

      },
      cyclingSpeed: this.cyclingSpeed,
      dailyCyclingHours: this.dailyCyclingHours
    };


    this.http.post<{ estimatedTimeInHours: number }>(url, requestBody, options).subscribe(
      (response:any) => {
        this.estimatedTime = response;
        this.accessibleMessage=response.message;
        this.timeHour= response?.estimatedTimeInHours;
      },
      (error) => {
        console.error('Calculation failed:', error);
        this.estimatedTime = null;
      }
    );
  }
}
<div class="container">
  <h2>Calculate Estimated Cycling Time</h2>

  <!-- Dropdown to select a spot -->
  <div class="form-group">
    <label for="chosenSpot">Choose a spot:</label>
    <select id="chosenSpot" [(ngModel)]="chosenSpot">
      <option *ngFor="let spot of spotDetails" [value]="spot.name">{{ spot.name }}</option>
    </select>
  </div>

  <!-- Input fields for user's location -->
  <div class="form-group">
    <label for="latitude">User's Latitude:</label>
    <input type="number" id="latitude" [(ngModel)]="userLocation.latitude" />
  </div>

  <div class="form-group">
    <label for="longitude">User's Longitude:</label>
    <input type="number" id="longitude" [(ngModel)]="userLocation.longitude" />
  </div>

  <div class="form-group">
    <label for="cyclingSpeed">Cycling Speed (km/h):</label>
    <input type="number" id="cyclingSpeed" [(ngModel)]="cyclingSpeed" />
  </div>

  <div class="form-group">
    <label for="dailyCyclingHours">Daily Cycling Hours:</label>
    <input type="number" id="dailyCyclingHours" [(ngModel)]="dailyCyclingHours" />
  </div>

  <button (click)="calculateTime()">Calculate</button>


  <div *ngIf="estimatedTime !== null">
    <p>{{ accessibleMessage }} </p>

  </div>
  <div *ngIf="timeHour">

    <p>Estimated Cycling Time: {{ timeHour | number: '1.1-2' }} hours</p>
  </div>
</div>
